#include <iostream>#include <cstdio>#include <cstring>#include <algorithm>using namespace std;int p[205];double q[205],t[205];bool s[205];int main (){    freopen("A-large.in","r",stdin);    freopen("out.out","w",stdout);    int T,n,sum,sss,num;    double ddd;	int cs=1;    scanf("%d",&T);    while(T--)	{        scanf("%d",&n);        sum=0;        for(int i=1; i<=n; i++)        {            s[i]=1;            scanf("%d",&p[i]);            sum+=p[i];        }        sss=sum;        num=n;        printf("Case #%d:",cs++);        for(int i=1; i<=n; ++i)            t[i]=double(p[i])/sum;        ddd=2/double(n);        for(int i=1; i<=n; ++i)            if(ddd<=t[i])            {                q[i]=0;                s[i]=0;                --num;                sss-=p[i];            }        ddd=double(sum+sss)/num;        for(int i=1;i<=n;++i)            if(s[i])            {                q[i]=(ddd-p[i])/sum;            }        for(int i=1; i<=n; ++i)            printf(" %.6lf",q[i]*100);        printf("\n");    }    return 0;}